import React, { Component } from "react";
import "../components/Components.css";
import logo from "../logo.svg";
import { FiTruck } from "react-icons/fi";
import { GrBasket } from "react-icons/gr";

class HeaderComponent extends Component {
  render() {
    return (
      <div class="header">
        <div>
          <a href="/Homepage">
            <img src={logo} class="logo" style={{ width: 60 }}></img>
          </a>
        </div>
        <div class="searchDiv">
          <form class="d-flex">
            <input
              class="form-control"
              type="search"
              placeholder="Aradığınız her şey burada"
            ></input>
            <button class="btn btn-outline-success" type="submit">
              Arama
            </button>
          </form>
        </div>
        <div
          class="rightDiv"
          style={{ justifyContent: "center", alignItems: "center" }}
        >
          <a class="rightDivItem header-link" href="#">
            <FiTruck style={{ marginRight: 5 }} />
            Sipariş Takip
          </a>
          <a class="rightDivItem header-link" href="/Login">
            Giriş Yap
          </a>
          <a class="rightDivItem header-link" href="/Basket">
            <GrBasket style={{ marginRight: 5 }} />
            Sepet
            {/* <span class="spanSepet">0</span> */}
          </a>
        </div>
      </div>
    );
  }
}
export default HeaderComponent;
