import axios from "axios";
import React, { useState } from "react";
import HeaderComponent from "../components/HeaderComponent.js";
import NavbarComponent from "../components/NavbarComponent.js";
import FooterComponent from "../components/FooterComponent.js";
import { AiFillSave } from "react-icons/ai";

function AddressDetail() {
  const [baslik, setBaslik] = useState("");
  const [sehir, setSehir] = useState("");
  const [ilce, setIlce] = useState("");
  const [mahalle, setMahalle] = useState("");
  const [telefon, setTelefon] = useState("");
  const [pkodu, setPkodu] = useState("");
  const [acikAdres, setAcikAdres] = useState("");
  const [model, setModel] = useState([]);

  const handleBaslik = (e) => {
    setBaslik(e.target.value);
  };
  const handleSehir = (e) => {
    setSehir(e.target.value);
  };
  const handleIlce = (e) => {
    setIlce(e.target.value);
  };
  const handleMahalle = (e) => {
    setMahalle(e.target.value);
  };
  const handleTelefon = (e) => {
    setTelefon(e.target.value);
  };
  const handlePkodu = (e) => {
    setPkodu(e.target.value);
  };
  const handleAcikAdres = (e) => {
    setAcikAdres(e.target.value);
  };

  const objAddress = {
    // Id: Id,
    baslik: baslik,
    sehir: sehir,
    ilce: ilce,
    mahalle: mahalle,
    telefon: telefon,
    pkodu: pkodu,
    acikAdres: acikAdres,
  };
  // const SendMessage = (e) => {
  //   e.preventDefault();
  //   if (mail == "" || subject == "" || text == "") {
  //     alert("Lütfen boş alan bırakmayın.");
  //   } else {
  //     model.push(obj);
  //     var API_PATH = "http://localhost:81/e-commerce-react/contact.php";
  //     axios
  //       .post(API_PATH, obj)
  //       .then((result) => {
  //         console.log(result);
  //         console.log("basarili= ", model);
  //         alert("mesajınız gönderildi.");
  //       })
  //       .catch((error) => {
  //         console.log(error);
  //         alert("hata.");
  //       });
  //   }
  // };

  return (
    <div>
      <HeaderComponent />
      <NavbarComponent />
      <div class="container uyecerceve">
        <div class="member-title">ADRES DÜZENLE</div>
        <div class="centerForm">
          <form
            // onSubmit={UserLogin}
            class="form-horizontal col-sm-offset-2 col-sm-7"
          >
            <div class="form-group">
              <label class="control-label col-sm-2" for="email">
                Email
              </label>
              <input
                type="email"
                class="form-control"
                name="mail"
                id="mail"
                placeholder=""
                required
                // onChange={handleMail}
              ></input>
            </div>

            <div class="form-group">
              <label class="control-label col-sm-2 marginTop-10" for="pwd">
                Şifre
              </label>
              <input
                type="password"
                class="form-control"
                name="password"
                id="password"
                placeholder=""
                required
                // onChange={handlePassword}
              ></input>
            </div>

            <div class="form-group sideBySide">
              <div class="btnuye-div">
                <button
                  type="submit"
                  name="submit"
                  class="btn btn-default btnuye btn-block"
                >
                  <AiFillSave style={{ marginRight: 10 }} />
                  Güncelle
                </button>
              </div>

              <div class="btnuye-div">
                <button
                  type="submit"
                  name="submit"
                  class="btn btn-default btnuye btn-block"
                >
                  <AiFillSave style={{ marginRight: 10 }} />
                  Sil
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>

      <FooterComponent />
    </div>
  );
}

export default AddressDetail;
