import data from "../data/data.js";
import { BrowserRouter } from "react-router-dom";
import axios from "axios";
import React, { useState, useEffect } from "react";

function Products(props) {
  const [model, setModel] = useState([]);

  useEffect(() => {
    GetProduct();
  }, []);

  const GetProduct = () => {
    var API_PATH = "http://localhost:81/e-commerce-react/getProducts.php";
    axios
      .post(API_PATH)
      .then((response) => {
        // response.data.response.map((modelList) => ({
        //   urun_Id: `${modelList.urun_Id}`,
        //   urun_Resim: `${modelList.urun_Resim}`,
        //   urun_Kod: `${modelList.urun_Kod}`,
        //   urun_Ozellik: `${modelList.urun_Ozellik}`,
        //   urun_Eski_Fiyat: `${modelList.urun_Eski_Fiyat}`,
        //   urun_Yeni_Fiyat: `${modelList.urun_Yeni_Fiyat}`,
        //   Kategori_Id: `${modelList.Kategori_Id}`,
        // }));
        const modelList = response.data.response;
        model.push(modelList);
        console.log("basarili= ", model);
      })
      // .then((model) => {
      //   setModel(model);
      // })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <BrowserRouter>
      <div
        class="container cerceve"
        style={{ display: "flex", flexDirection: "row" }}
      >
        <div class="col-md-3" style={{ marginRight: 20 }}>
          SIRALAMALAR
          <p>fiyat</p>
          <p>kategori</p>
          <p>3</p>
          <p>4</p>
          <div class="form-group marginTop-10">
            <label>Renk</label>
            <select
              class="form-control"
              style={{
                width: 240,
                height: 40,
              }}
            >
              <option>Hepsi</option>
              <option>Beyaz</option>
              <option>Siyah</option>
              <option>Kırmızı</option>
              <option>Yeşil</option>
            </select>
          </div>
          <div class="form-group marginTop-10">
            <label>Boyut</label>
            <select
              class="form-control hasCustomSelect"
              style={{
                width: 240,
                height: 40,
              }}
            >
              <option>Hepsi</option>
              <option>Küçük</option>
              <option>Orta</option>
              <option>Büyük</option>
            </select>
          </div>
        </div>
        <div class="col-md-9">
          <div class="row product-list">
            {model.map((getProductData) => (
              <div class="col-sm-4">
                <a
                  key={getProductData.urun_Id.toString()}
                  style={{ textDecoration: "none" }}
                  href={"/ProductView/" + getProductData.urun_Id}
                >
                  <div class="urun-cerceve">
                    <div class="urun-resim">
                      <img src={getProductData.urun_Resim} alt="" />
                    </div>
                    <div class="urun-metin">
                      <span>
                        {getProductData.urun_Kod} - {getProductData.urun_Ad}
                      </span>
                      <br></br>
                    </div>
                    <hr></hr>
                    <div class="urun-bottom">
                      <span class="urun-eski-fiyat">
                        <del>{getProductData.urun_Eski_Fiyat}</del>
                      </span>
                      <br></br>
                      <span class="urun-yeni-fiyat">
                        {getProductData.urun_Yeni_Fiyat}
                      </span>
                    </div>
                  </div>
                </a>
              </div>
            ))}
          </div>
        </div>
      </div>
    </BrowserRouter>
  );
}
export default Products;
