import { AiFillLock } from "react-icons/ai";
import axios from "axios";
import React, { useState } from "react";
import Cards from "react-credit-cards";
import CreditCard from "../css/CreditCard.css";

function AddCreditCard() {
  const [cvc, setCvc] = useState("");
  const [expiry, setExpiry] = useState("");
  const [focus, setFocus] = useState("");
  const [name, setName] = useState("");
  const [number, setNumber] = useState("");

  const handleInputFocus = (e) => {
    setFocus(e.target.value);
  };

  const handleCvc = (e) => {
    setCvc(e.target.value);
  };
  const handleExpiry = (e) => {
    setExpiry(e.target.value);
  };
  const handleName = (e) => {
    setName(e.target.value);
  };
  const handleNumber = (e) => {
    setNumber(e.target.value);
  };

  // handleInputChange = (e) => {
  //   const { name, value } = e.target;
  //   this.setState({ [name]: value });
  // };

  return (
    <div class="container uyecerceve">
      <div class="row">
        <div class="col-sm-6">
          <h2 style={{ textAlign: "center" }}>Bize </h2>
        </div>

        <div class="col-sm-6" style={{ marginTop: 30 }}>
          <form>
            <input
              type="number"
              name="number"
              placeholder="Kart NumarasÄ±"
              onChange={handleNumber}
              onFocus={handleInputFocus}
              maxLength="16"
            />
            <input
              type="text"
              name="namelastname"
              placeholder="Ad Soyad"
              onChange={handleName}
              onFocus={handleInputFocus}
            />
            <input
              type="number"
              name="tarih"
              placeholder="Tarih"
              onChange={handleExpiry}
              onFocus={handleInputFocus}
            />
            <input
              type="number"
              name="cvc"
              placeholder="Cvc"
              onChange={handleCvc}
              onFocus={handleInputFocus}
            />
          </form>
        </div>
      </div>
    </div>

    // <div class="container uyecerceve">
    //   <div class="member-title">KART EKLE</div>
    //   <div class="centerForm">
    //     <div class="row">
    //       <div id="PaymentForm">
    //         <div class="col-sm-6">
    //           <Cards
    //             cvc={cvc}
    //             expiry={expiry}
    //             focused={focus}
    //             name={name}
    //             number={number}
    //           />
    //         </div>

    //         <form
    //           // onSubmit={UserLogin}
    //           class="form-horizontal col-sm-offset-2 col-sm-7"
    //         >
    //           <div class="form-group">
    //             <label class="control-label" for="email">
    //               Email
    //             </label>
    //             <input
    //               type="email"
    //               class="form-control"
    //               name="mail"
    //               id="mail"
    //               placeholder=""
    //               required
    //             ></input>
    //           </div>

    //           <div class="form-group">
    //             <label class="control-label marginTop-10" for="pwd">
    //               Åžifre
    //             </label>
    //             <input
    //               type="password"
    //               class="form-control"
    //               name="password"
    //               id="password"
    //               placeholder=""
    //               required
    //             ></input>
    //           </div>
  );
}

export default AddCreditCard;
