import React, { Component } from "react";
import "../components/Components.css";
import logo from "../logo.svg";

class SliderComponent extends Component {
  render() {
    return (
      <div class="container">
        <div id="myCarousel" class="carousel slide" data-ride="carousel">
          {/* Indicators */}
          <ol class="carousel-indicators">
            <li data-target="#myCarousel" data-slide-to="0" class="active"></li>
            <li data-target="#myCarousel" data-slide-to="1"></li>
            <li data-target="#myCarousel" data-slide-to="2"></li>
            <li data-target="#myCarousel" data-slide-to="3"></li>
          </ol>

          {/* Wrapper for slides */}
          <div class="carousel-inner">
            <div class="item active">
              <img
                src="../images/slayt1.jpg"
                alt="Los Angeles"
                style="height:400px; width:100%;"
              />
            </div>

            <div class="item">
              <img
                src="../images/slayt2.jpg"
                alt="Chicago"
                style="height:400px; width:100%;"
              />
            </div>

            <div class="item">
              <img
                src="../images/slayt3.jpg"
                alt="New York"
                style="height:400px; width:100%;"
              />
            </div>

            <div class="item">
              <img
                src="../images/slayt4.jpg"
                alt="New York"
                style="height:400px; width:100%;"
              />
            </div>
          </div>

          {/* Sol ve Sağ Kontroller */}
          <a class="left carousel-control" href="#myCarousel" data-slide="prev">
            <span class="glyphicon glyphicon-chevron-left"></span>
            <span class="sr-only">Geri</span>
          </a>
          <a
            class="right carousel-control"
            href="#myCarousel"
            data-slide="next"
          >
            <span class="glyphicon glyphicon-chevron-right"></span>
            <span class="sr-only">İleri</span>
          </a>
        </div>
      </div>
      // <div class="header">
      //   <div>
      //     <img src={logo} class="logo" style={{ width: 60 }}></img>
      //   </div>
      //   <div class="searchDiv">
      //     <form class="d-flex">
      //       <input
      //         class="form-control"
      //         type="search"
      //         placeholder="Aradığınız her şey burada"
      //       ></input>
      //       <button class="btn btn-outline-success" type="submit">
      //         Arama
      //       </button>
      //     </form>
      //   </div>
      //   <div class="rightDiv">
      //     <a class="rightDivItem header-link" href="#">
      //       Sipariş Takip
      //     </a>
      //     <a class="rightDivItem header-link" href="#">
      //       Giriş Yap
      //     </a>
      //     <a class="rightDivItem header-link" href="#">
      //       Sepet
      //       <span class="spanSepet">0</span>
      //     </a>
      //   </div>
      // </div>
    );
  }
}
export default SliderComponent;
