import axios from "axios";

const addToCart = (productId, qty) => async (dispatchEvent) => {
  try {
    const { data } = await axios.get("/api/products/" + productId);
    dispatchEvent({
      type: CART_ADD_ITEM,
      payload: {
        product: data.Id,
        name: data.name,
        model: data.model,
        code: data.code,
        src: data.src,
        lastPrice: data.lastPrice,
        price: data.price,
        totalCount: data.totalCount,
        qty,
      },
    });
  } catch (error) {}
};

export { addToCart };
