// import "../css/Pages.css";
// import "../css/Bootstrap.css";
// import "../css/BooststrapFive.css";
import { BiTrash, BiArrowBack, BiCartAlt } from "react-icons/bi";
import product from "../data/data.js";
import data from "../data/data.js";
import { Link, BrowserRouter } from "react-router-dom";

function Products(props) {
  return (
    <BrowserRouter>
      <div
        class="container cerceve"
        style={{ display: "flex", flexDirection: "row" }}
      >
        <div class="col-md-3" style={{ marginRight: 20 }}>
          SIRALAMALAR
          <p>fiyat</p>
          <p>kategori</p>
          <p>3</p>
          <p>4</p>
          <div class="form-group marginTop-10">
            <label>Renk</label>
            <select
              class="form-control"
              style={{
                width: 240,
                height: 40,
              }}
            >
              <option>Hepsi</option>
              <option>Beyaz</option>
              <option>Siyah</option>
              <option>Kırmızı</option>
              <option>Yeşil</option>
            </select>
          </div>
          <div class="form-group marginTop-10">
            <label>Boyut</label>
            <select
              class="form-control hasCustomSelect"
              style={{
                width: 240,
                height: 40,
              }}
            >
              <option>Hepsi</option>
              <option>Küçük</option>
              <option>Orta</option>
              <option>Büyük</option>
            </select>
          </div>
        </div>
        <div class="col-md-9">
          <div class="row product-list">
            {data.productData.map((getProductData) => (
              <div class="col-sm-4">
                <a
                  key={getProductData.Id.toString()}
                  style={{ textDecoration: "none" }}
                  href={"/ProductView/" + getProductData.Id}
                >
                  {/* <Link to={"/ProductView/" + getProductData.Id}> */}
                  <div class="urun-cerceve">
                    <div class="urun-resim">
                      <img src={getProductData.src} alt="" />
                    </div>
                    <div class="urun-metin">
                      <span>{getProductData.name}</span>
                      <br></br>
                      <span>
                        {getProductData.code} - {getProductData.model}
                      </span>
                    </div>
                    <hr></hr>
                    <div class="urun-bottom">
                      <span class="urun-eski-fiyat">
                        <del>{getProductData.lastPrice}</del>
                      </span>
                      <br></br>
                      <span class="urun-yeni-fiyat">
                        {getProductData.price}
                      </span>
                    </div>
                  </div>
                </a>
                {/* </Link> */}
              </div>
            ))}
          </div>
        </div>
      </div>
    </BrowserRouter>
  );
}
export default Products;
