import React, { Component } from "react";
import {
  AiFillFacebook,
  AiFillTwitterSquare,
  AiFillMail,
  AiFillPhone,
  AiFillInstagram,
} from "react-icons/ai";
import { IoLocationSharp } from "react-icons/io5";
import { FaFacebook, FaTwitter, FaInstagram } from "react-icons/fa";

class FooterComponent extends Component {
  render() {
    return (
      <div class="container-fluid footer1" style={{ marginTop: 10 }}>
        <div class="container">
          <div class="row">
            <div class="col-md-3 ">
              <h3 class="footer-title">MÜŞTERİ HİZMETLERİ</h3>
              <ul class="list-unstyled">
                <li>
                  <a href="#" class="footer-link">
                    Anlaşmalı Bankalar
                  </a>
                </li>
                <li>
                  <a href="#" class="footer-link">
                    Destek, Öneri ve Şikayet
                  </a>
                </li>
                <li>
                  <a href="#" class="footer-link">
                    Sıkça Sorulan Sorular
                  </a>
                </li>
                <li>
                  <a href="#" class="footer-link">
                    Sipariş Takibi
                  </a>
                </li>
                <li>
                  <a href="/Contact" class="footer-link">
                    İletişim
                  </a>
                </li>
              </ul>
            </div>

            <div class="col-md-3">
              <h3 class="footer-title">KURUMSAL</h3>
              <ul class="list-unstyled">
                <li>
                  <a href="#" class="footer-link">
                    Hakkımızda
                  </a>
                </li>
                <li>
                  <a href="#" class="footer-link">
                    İnsan Kaynakları
                  </a>
                </li>
                <li>
                  <a href="#" class="footer-link">
                    Kariyer
                  </a>
                </li>
                <li>
                  <a href="#" class="footer-link">
                    Müşteri Hizmetleri
                  </a>
                </li>
                <li>
                  <a href="#" class="footer-link">
                    Bayi Girişi
                  </a>
                </li>
              </ul>
            </div>

            <div class="col-md-3">
              <h3 class="footer-title">SIK SORULAN SORULAR</h3>
              <ul class="list-unstyled">
                <li>
                  <a href="#" class="footer-link">
                    Kargo Takibi
