import axios from "axios";
import React, { useState } from "react";
import HeaderComponent from "../components/HeaderComponent.js";
import NavbarComponent from "../components/NavbarComponent.js";
import FooterComponent from "../components/FooterComponent.js";
import { AiFillSave } from "react-icons/ai";

function AddressDetail() {
  const [baslik, setBaslik] = useState("");
  const [sehir, setSehir] = useState("");
  const [ilce, setIlce] = useState("");
  const [mahalle, setMahalle] = useState("");
  const [telefon, setTelefon] = useState("");
  const [pkodu, setPkodu] = useState("");
  const [acikAdres, setAcikAdres] = useState("");
  const [model, setModel] = useState([]);

  const handleBaslik = (e) => {
    setBaslik(e.target.value);
  };
  const handleSehir = (e) => {
    setSehir(e.target.value);
  };
  const handleIlce = (e) => {
    setIlce(e.target.value);
  };
  const handleMahalle = (e) => {
    setMahalle(e.target.value);
  };
  const handleTelefon = (e) => {
    setTelefon(e.target.value);
  };
  const handlePkodu = (e) => {
    setPkodu(e.target.value);
  };
  const handleAcikAdres = (e) => {
    setAcikAdres(e.target.value);
  };

  const objAddress = {
    // Id: Id,
    baslik: baslik,
    sehir: sehir,
    ilce: ilce,
    mahalle: mahalle,
    telefon: telefon,
    pkodu: pkodu,
    acikAdres: acikAdres,
  };
  // const SendMessage = (e) => {
  //   e.preventDefault();
  //   if (mail == "" || subject == "" || text == "") {
  //     alert("Lütfen boş alan bırakmayın.");
  //   } else {
  //     model.push(obj);
  //     var API_PATH = "http://localhost:81/e-commerce-react/contact.php";
  //     axios
  //       .post(API_PATH, obj)
  //       .then((result) => {
  //         console.log(result);
  //         console.log("basarili= ", model);
  //         alert("mesajınız gönderildi.");
  //       })
  //       .catch((error) => {
  //         console.log(error);
  //         alert("hata.");
  //       });
  //   }
  // };

  return (
    <div>
      <HeaderComponent />
      <NavbarComponent />
      <div class="container uyecerceve">
        <div class="member-title">YENİ ADRES</div>
        <div class="centerForm">
          <form
            // onSubmit={AddAddress}
            class="form-horizontal col-sm-offset-2 col-sm-12"
          >
            <div class="form-group">
              <label class="control-label col-sm-5">Adres başlığı</label>
              <input
                type="text"
                class="form-control"
                id="baslik"
                name="baslik"
                placeholder=""
                required
                onChange={handleBaslik}
              />
            </div>
            <div class="form-group">
              <label class="control-label col-sm-5 marginTop-10">Şehir</label>
              <input
                type="text"
                class="form-control"
                id="sehir"
                name="sehir"
                placeholder=""
                required
                onChange={handleSehir}
              />
            </div>
            <div class="form-group">
              <label class="control-label col-sm-5 marginTop-10">İlçe</label>
              <input
                type="text"
                class="form-control"
                id="ilce"
                name="ilce"
                placeholder=""
                required
                onChange={handleIlce}
              />
            </div>
            <div class="form-group">
              <label class="control-label col-sm-5 marginTop-10">Mahalle</label>
              <input
                type="text"
                class="form-control"
                id="mahalle"
                name="mahalle"
                placeholder=""
                required
                onChange={handleMahalle}
              />
            </div>
            <div class="form-group">
              <label class="control-label col-sm-5 marginTop-10">Telefon</label>
              <input
                type="number"
                class="form-control"
                id="telefon"
                name="telefon"
                placeholder=""
                required
                onChange={handleTelefon}
              />
            </div>
            <div class="form-group">
              <label class="control-label col-sm-5 marginTop-10">
                Posta Kodu
              </label>
              <input
                type="number"
                class="form-control"
                id="pkodu"
                name="pkodu"
                placeholder=""
                required
                onChange={handlePkodu}
              />
            </div>
            <div class="form-group">
              <label class="control-label col-sm-5 marginTop-10">
                Açık Adres
              </label>
              <input
                type="text"
                class="form-control"
                id="acikAdres"
                name="acikAdres"
                placeholder=""
                required
                onChange={handleAcikAdres}
              />
            </div>
            <div class="modal-footer">
              <button
                type="button"
                class="btnuye"
                style={{
                  width: 80,
                  height: 40,
                  color: "white",
                  backgroundColor: "gray",
                  borderColor: "gray",
                }}
                data-dismiss="modal"
              >
                Çıkış
              </button>
              <button
                type="submit"
                name="submit"
                class="btnuye"
                style={{ width: 80, height: 40 }}
              >
                Ekle
              </button>
            </div>
          </form>
        </div>
      </div>

      <div class="container uyecerceve">
        <div class="member-title">ADRES DÜZENLE</div>
        <div class="centerForm">
          <form
            // onSubmit={UserLogin}
            class="form-horizontal col-sm-offset-2 col-sm-7"
          >
            <div class="form-group">
              <label class="control-label col-sm-2" for="email">
                Email
              </label>
              <input
                type="email"
                class="form-control"
                name="mail"
                id="mail"
                placeholder=""
                required
                // onChange={handleMail}
              ></input>
            </div>

            <div class="form-group">
              <label class="control-label col-sm-2 marginTop-10" for="pwd">
                Şifre
              </label>
              <input
                type="password"
                class="form-control"
                name="password"
                id="password"
                placeholder=""
                required
                // onChange={handlePassword}
              ></input>
            </div>

            <div class="form-group sideBySide">
              <div class="btnuye-div">
                <button
                  type="submit"
                  name="submit"
                  class="btn btn-default btnuye btn-block"
                >
                  <AiFillSave style={{ marginRight: 10 }} />
                  Güncelle
                </button>
              </div>

              <div class="btnuye-div">
                <button
                  type="submit"
                  name="submit"
                  class="btn btn-default btnuye btn-block"
                >
                  <AiFillSave style={{ marginRight: 10 }} />
                  Sil
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>

      <FooterComponent />
    </div>
  );
}

export default AddressDetail;
