import data from "../data/data";
import axios from "axios";
import React, { useState, useEffect } from "react";

function ProductView(props) {
  const [model, setModel] = useState([]);
  const [testProduct, setTestProduct] = useState([]);

  // const selectedProduct = model.find(
  //   (i) => i.urun_Id == props.match.params.urun_Id
  // );

  useEffect(() => {
    GetProduct();
  }, []);

  const GetProduct = () => {
    var API_PATH = "http://localhost:81/e-commerce-react/getProducts.php";
    axios
      .post(API_PATH)
      .then((response) => {
        const modelList = response.data;
        setModel(modelList);
        setTestProduct(
          model.find((i) => (i.urun_Id = props.match.params.urun_Id))
        );
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div
      class="container cerceve"
      style={{ display: "flex", flexDirection: "row" }}
    >
      <form class="form-inline">
        <div class="col-sm-12">
          <div class="urun-incele-resim">
            <a href="#">
              <img src={selectedProduct.urun_Resim} alt=""></img>
            </a>
          </div>
        </div>

        <div class="col-sm-12">
          <h1 class="incele-ad">{selectedProduct.urun_Ad}</h1>
          <br></br>
          <span class="incele-kod">Ürün Kodu: {selectedProduct.urun_Kod}</span>
          <br></br>
          <br></br>
          <span class="incele-kod">
            Adet Fiyatı:
            <del class="incele-eski-fiyat">
              {selectedProduct.urun_Eski_Fiyat} TL
            </del>
          </span>
          <br></br>
          <br></br>
          <span class="incele-yeni-fiyat">
            İndirimli Adet Fiyatı:
            <span class="incele-yeni-fiyat2" style={{ marginLeft: 10 }}>
              {selectedProduct.urun_Yeni_Fiyat} TL
            </span>
          </span>
          <hr></hr>
        </div>
        <br></br>
        <div class="col-sm-12 form-group" style={{ marginLeft: 20 }}>
          <button
            class="btn btn-default"
            style={{
              width: 250,
              backgroundColor: "#ad6290",
              height: 45,
              color: "white",
              fontSize: 15,
              fontWeight: "bold",
            }}
          >
            SEPETE EKLE
          </button>
        </div>
      </form>
    </div>
  );
}
export default ProductView;
